import React from 'react';
import Info from "./Components/info";
import Form from "./Components/form";
import Weather from "./Components/weather";

// const API_KEY = "4b8c4084a4a690e55a8fbc77fb37ce5d";

class App extends React.Component {

    gettingWeather = async (event) => {
        event.preventDefault(); //Перезагрузка страницы будет уничтожена.
        event.target

        //Fetch - позволяет прочитать URL-адрес и Получить из него данные
        const api_url = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London&appid=4b8c4084a4a690e55a8fbc77fb37ce5d`);
        const data = await api_url.json();
        console.log(data);
    }

    render() {
        return (
            <div>
                <Info />
                <Form weatherMethod={this.gettingWeather} />
                <Weather />
            </div>

        );
    }
}

export default App;
